// import mongodb from 'mongodb';
const { MongoClient } = require('mongodb');

class DBClient {
  constructor() {
    const host = process.env.DB_HOST || 'localhost';
    const port = process.env.DB_PORT || 27017;
    const database = process.env.DB_DATABASE || 'files_manager';

    const uri = `mongodb://${host}:${port}/${database}`;

    this.client = new MongoClient(uri, {
      useNewUrlParser: true,
      useUnifiedTopology: true,
    });

    this.db = null;
    this.usersCollection = null;
    this.filesCollection = null;
  }

  async connect() {
    try {
      await this.client.connect();
      this.db = this.client.db();
      this.usersCollection = this.db.collection('users');
      this.filesCollection = this.db.collection('files');
      console.log('Connected to MongoDB');
    } catch (err) {
      console.error('Error connecting to MongoDB:', err.message);
      throw err;
    }
  }

  async isAlive() {
    return this.client.isConnected();
  }

  async nbUsers() {
    try {
      const count = await this.usersCollection.countDocuments();
      return count;
    } catch (err) {
      console.error('Error counting documents in users collection:', err.message);
      throw err;
    }
  }

  async nbFiles() {
    try {
      const count = await this.filesCollection.countDocuments();
      return count;
    } catch (err) {
      console.error('Error counting documents in files collection:', err.message);
      throw err;
    }
  }
}

// Create an instance of DBClient and connect to MongoDB
const dbClient = new DBClient();
dbClient.connect().catch(console.error);

export default dbClient;

