sourcesContent:
const redis = require('redis');

class RedisClient {
  constructor() {
    const host = process.env.REDIS_HOST || 'localhost';
    const port = process.env.REDIS_PORT || 6379;

    this.client = redis.createClient({ host, port });

    // Handle Redis client errors
    this.client.on('error', (err) => {
      console.error('Redis error:', err.message);
    });
  }

  async isAlive() {
    try {
      const reply = await this.client.ping();
      return reply === 'PONG';
    } catch (err) {
      console.error('Error pinging Redis:', err.message);
      return false;
    }
  }

  async get(key) {
    return new Promise((resolve, reject) => {
      this.client.get(key, (err, reply) => {
        if (err) {
          console.error('Error getting value from Redis:', err.message);
          reject(err);
        } else {
          resolve(reply);
        }
      });
    });
  }

  async set(key, value, durationInSeconds) {
    return new Promise((resolve, reject) => {
      this.client.set(key, value, 'EX', durationInSeconds, (err, reply) => {
        if (err) {
          console.error('Error setting value in Redis:', err.message);
          reject(err);
        } else {
          resolve(reply === 'OK');
        }
      });
    });
  }

  async del(key) {
    return new Promise((resolve, reject) => {
      this.client.del(key, (err, reply) => {
        if (err) {
          console.error('Error deleting value from Redis:', err.message);
          reject(err);
        } else {
          resolve(reply === 1);
        }
      });
    });
  }
}

// Create an instance of RedisClient
const redisClient = new RedisClient();

module.exports = redisClient;

